using UnityEngine;using UnityEngine.UI;namespace Toolkit{    public class ChildrenColorButton : Button    {        Graphic[] graphics;        protected override void Awake()        {            graphics = GetComponentsInChildren<Graphic>();        }        protected override void DoStateTransition(SelectionState state, bool instant)        {            base.DoStateTransition(state, instant);            Color color = Color.white;            switch (state)            {                case SelectionState.Normal:                    color = colors.normalColor;                    break;                case SelectionState.Highlighted:                    color = colors.highlightedColor;                    break;                case SelectionState.Pressed:                    color = colors.pressedColor;                    break;                case SelectionState.Selected:                    color = colors.selectedColor;                    break;                case SelectionState.Disabled:                    color = colors.disabledColor;                    break;            }            foreach (var graphic in graphics)            {                graphic.CrossFadeColor(color, instant ? 0f : colors.fadeDuration, true, true);            }        }    }}